
public class onedimensionArray {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
//		two ways to add no in Arrays
		int[] arr=new int[4];
		arr[0]=10;
		arr[1]=20;
		arr[2]=30;
		arr[3]=40;
		System.out.println("Element at index 0:"+arr[0]);
		System.out.println("Element at index 1:"+arr[1]);
		System.out.println("Element at index 2:"+arr[2]);
		System.out.println("Element at index 3:"+arr[3]);
		
		int [] arr2= {45,48,50,54};

		System.out.println("Element at index 0:"+arr2[0]);
		System.out.println("Element at index 1:"+arr2[1]);
		System.out.println("Element at index 2:"+arr2[2]);
		System.out.println("Element at index 3:"+arr2[3]);
		
		
		
	}

}
